function mostrarResultado(e){document.getElementById("resultado-equacao").innerText=e,document.querySelector(".resultado").style.display="block"}document.addEventListener("DOMContentLoaded",function(){var e=document.getElementById("form"),t=document.getElementById("soma"),o=document.getElementById("subtracao"),n=document.getElementById("multiplicacao"),r=document.getElementById("divisao");function u(){return{numeroUm:Number(document.getElementById("valor-um").value),numeroDois:Number(document.getElementById("valor-dois").value)}}e.addEventListener("submit",function(e){e.preventDefault()}),t.addEventListener("click",function(){var{numeroUm:e,numeroDois:t}=u();mostrarResultado(e+t)}),o.addEventListener("click",function(){var{numeroUm:e,numeroDois:t}=u();mostrarResultado(e-t)}),n.addEventListener("click",function(){var{numeroUm:e,numeroDois:t}=u();mostrarResultado(e*t)}),r.addEventListener("click",function(){var{numeroUm:e,numeroDois:t}=u();0===t?alert("Não é possível dividir por zero!"):mostrarResultado((e/t).toFixed(1))})});